import { test, expect } from '@playwright/test';
import { AuthUtils, ChatUtils, WaitUtils, DataUtils, AssertionUtils, PerformanceUtils } from '../../utils/test-utils';

test.describe('Chat Functionality', () => {
  let authUtils: AuthUtils;
  let chatUtils: ChatUtils;
  let waitUtils: WaitUtils;
  let performanceUtils: PerformanceUtils;
  let testUsers: Record<string, any>;

  test.beforeEach(async ({ page }) => {
    authUtils = new AuthUtils(page);
    chatUtils = new ChatUtils(page);
    waitUtils = new WaitUtils(page);
    performanceUtils = new PerformanceUtils(page);
    testUsers = DataUtils.loadTestUsers();

    // Login with paid user for full functionality
    await authUtils.loginWithGoogle(testUsers.paid.email, testUsers.paid.password);
  });

  test.afterEach(async ({ page }) => {
    // Clean up test data
    await DataUtils.cleanupTestData(page);
  });

  test('should send and receive basic chat messages', async ({ page }) => {
    const testMessage = 'Hello, this is a test message. Please respond with "Test response received."';

    await chatUtils.sendMessage(testMessage);

    // Verify message was sent
    const messages = await chatUtils.getAllMessages();
    expect(messages).toHaveLength(2); // User message + AI response
    expect(messages[0].content).toContain(testMessage);
    expect(messages[1].role).toBe('assistant');
    expect(messages[1].content.length).toBeGreaterThan(0);
  });

  test('should handle streaming responses correctly', async ({ page }) => {
    const testMessage = 'Write a short story about a robot learning to paint.';

    // Send message and monitor streaming
    await page.fill('[data-testid="chat-input"]', testMessage);
    await page.click('[data-testid="send-button"]');

    // Verify streaming indicator appears
    await expect(page.locator('[data-testid="message-loading"]')).toBeVisible();

    // Wait for streaming to complete
    await waitUtils.waitForAIResponse();

    // Verify final response
    const lastMessage = await chatUtils.getLastMessage();
    expect(lastMessage.length).toBeGreaterThan(50); // Should be a substantial response
  });

  test('should maintain conversation context', async ({ page }) => {
    // Send first message
    await chatUtils.sendMessage('My name is TestUser. Remember this.');

    // Send follow-up message
    await chatUtils.sendMessage('What is my name?');

    // AI should remember the name from previous message
    const response = await chatUtils.getLastMessage();
    expect(response.toLowerCase()).toContain('testuser');
  });

  test('should handle long messages correctly', async ({ page }) => {
    const longMessage = 'This is a very long message. '.repeat(100);

    await chatUtils.sendMessage(longMessage);

    // Verify message was sent and response received
    const messages = await chatUtils.getAllMessages();
    expect(messages[0].content).toContain('This is a very long message.');
    expect(messages[1].role).toBe('assistant');
  });

  test('should handle special characters and emojis', async ({ page }) => {
    const specialMessage = 'Test with special chars: @#$%^&*()_+ and emojis: ';

    await chatUtils.sendMessage(specialMessage);

    const messages = await chatUtils.getAllMessages();
    expect(messages[0].content).toBe(specialMessage);
  });

  test('should create and manage conversations', async ({ page }) => {
    const conversationTitle = `Test Conversation ${DataUtils.generateUniqueId()}`;

    // Create new conversation
    await chatUtils.createNewConversation(conversationTitle);

    // Send a message in the new conversation
    await chatUtils.sendMessage('This is a message in the new conversation.');

    // Verify conversation appears in sidebar
    await AssertionUtils.assertConversationExists(page, conversationTitle);

    // Switch to another conversation and back
    await chatUtils.createNewConversation();
    await chatUtils.selectConversation(conversationTitle);

    // Verify message is still there
    const messages = await chatUtils.getAllMessages();
    expect(messages[0].content).toContain('This is a message in the new conversation.');
  });

  test('should delete conversations', async ({ page }) => {
    const conversationTitle = `Delete Test ${DataUtils.generateUniqueId()}`;

    // Create and populate conversation
    await chatUtils.createNewConversation(conversationTitle);
    await chatUtils.sendMessage('This conversation will be deleted.');

    // Delete conversation
    await chatUtils.deleteConversation(conversationTitle);

    // Verify conversation is gone
    await page.goto('/conversations');
    await expect(page.locator(`[data-testid="conversation-item"][data-title="${conversationTitle}"]`)).not.toBeVisible();
  });

  test('should clear chat history', async ({ page }) => {
    // Send multiple messages
    await chatUtils.sendMessage('First message');
    await chatUtils.sendMessage('Second message');

    // Clear chat
    await chatUtils.clearChat();

    // Verify chat is cleared
    const messages = await chatUtils.getAllMessages();
    expect(messages).toHaveLength(0);
  });

  test('should handle model switching', async ({ page }) => {
    // Switch to different model (if available for user tier)
    await page.click('[data-testid="model-selector"]');

    const modelOptions = await page.locator('[data-testid="model-option"]').all();
    if (modelOptions.length > 1) {
      await modelOptions[1].click();

      // Send message with new model
      await chatUtils.sendMessage('Test message with different model');

      // Verify response is received
      const response = await chatUtils.getLastMessage();
      expect(response.length).toBeGreaterThan(0);
    }
  });

  test('should handle file attachments', async ({ page }) => {
    // Create a test file
    const testFilePath = 'test-data/documents/test-document.txt';

    // Upload file
    const fileInput = page.locator('[data-testid="file-upload-input"]');
    await fileInput.setInputFiles(testFilePath);

    // Send message with attachment
    await chatUtils.sendMessage('Please analyze this document.');

    // Verify response acknowledges the attachment
    const response = await chatUtils.getLastMessage();
    expect(response.toLowerCase()).toMatch(/(document|file|attachment)/);
  });

  test('should respect user tier limitations', async ({ page }) => {
    // Logout and login as free user
    await authUtils.logout();
    await authUtils.loginWithGoogle(testUsers.free.email, testUsers.free.password);

    // Free users should only have access to Llama model
    await expect(page.locator('[data-testid="model-selector"]')).toContainText('Llama');

    // Send message and verify it works
    await chatUtils.sendMessage('Test message as free user');
    const response = await chatUtils.getLastMessage();
    expect(response.length).toBeGreaterThan(0);
  });

  test('should handle network interruptions gracefully', async ({ page }) => {
    // Start sending a message
    await page.fill('[data-testid="chat-input"]', 'Test message during network interruption');

    // Simulate network interruption
    await page.context().setOffline(true);
    await page.click('[data-testid="send-button"]');

    // Should show error or retry mechanism
    await expect(page.locator('[data-testid="network-error"]')).toBeVisible({ timeout: 10000 });

    // Restore network
    await page.context().setOffline(false);

    // Should be able to retry
    await page.click('[data-testid="retry-button"]');
    await waitUtils.waitForAIResponse();

    const response = await chatUtils.getLastMessage();
    expect(response.length).toBeGreaterThan(0);
  });

  test('should measure response time performance', async ({ page }) => {
    const testMessage = 'What is the capital of France?';

    const responseTime = await performanceUtils.measureAIResponseTime(testMessage);

    // Response should be within acceptable time limit (30 seconds)
    expect(responseTime).toBeLessThan(30000);

    // Log performance metrics
    console.log(`AI Response Time: ${responseTime}ms`);
  });

  test('should handle concurrent messages', async ({ page, context }) => {
    // Create multiple pages for concurrent testing
    const page2 = await context.newPage();
    const chatUtils2 = new ChatUtils(page2);

    // Login on second page
    const authUtils2 = new AuthUtils(page2);
    await authUtils2.loginWithGoogle(testUsers.paid.email, testUsers.paid.password);

    // Send messages concurrently
    await Promise.all([
      chatUtils.sendMessage('Concurrent message 1'),
      chatUtils2.sendMessage('Concurrent message 2')
    ]);

    // Both should receive responses
    const response1 = await chatUtils.getLastMessage();
    const response2 = await chatUtils2.getLastMessage();

    expect(response1.length).toBeGreaterThan(0);
    expect(response2.length).toBeGreaterThan(0);

    await page2.close();
  });

  test('should preserve chat state during page navigation', async ({ page }) => {
    // Send a message
    await chatUtils.sendMessage('Test message for navigation');

    // Navigate away and back
    await page.goto('/agents');
    await page.goto('/');

    // Message should still be there
    const messages = await chatUtils.getAllMessages();
    expect(messages[0].content).toContain('Test message for navigation');
  });

  test('should handle markdown formatting in responses', async ({ page }) => {
    await chatUtils.sendMessage('Please respond with markdown formatting including **bold**, *italic*, and `code`.');

    const lastMessageElement = page.locator('[data-testid="message-content"]').last();

    // Check for rendered markdown elements
    await expect(lastMessageElement.locator('strong')).toBeVisible();
    await expect(lastMessageElement.locator('em')).toBeVisible();
    await expect(lastMessageElement.locator('code')).toBeVisible();
  });

  test('should handle code blocks in responses', async ({ page }) => {
    await chatUtils.sendMessage('Write a simple Python function to add two numbers.');

    const lastMessageElement = page.locator('[data-testid="message-content"]').last();

    // Should contain code block
    await expect(lastMessageElement.locator('pre code')).toBeVisible();

    // Should have syntax highlighting
    await expect(lastMessageElement.locator('.hljs')).toBeVisible();
  });
});

test.describe('Chat Error Handling', () => {
  let authUtils: AuthUtils;
  let chatUtils: ChatUtils;

  test.beforeEach(async ({ page }) => {
    authUtils = new AuthUtils(page);
    chatUtils = new ChatUtils(page);

    const testUsers = DataUtils.loadTestUsers();
    await authUtils.loginWithGoogle(testUsers.paid.email, testUsers.paid.password);
  });

  test('should handle API errors gracefully', async ({ page }) => {
    // Mock API error
    await page.route('**/bedrock-runtime/**', route => {
      route.fulfill({
        status: 500,
        body: JSON.stringify({ error: 'Internal Server Error' })
      });
    });

    await chatUtils.sendMessage('This should trigger an error');

    // Should show error message
    await expect(page.locator('[data-testid="error-message"]')).toBeVisible();
    await expect(page.locator('[data-testid="retry-button"]')).toBeVisible();
  });

  test('should handle rate limiting', async ({ page }) => {
    // Mock rate limit response
    await page.route('**/bedrock-runtime/**', route => {
      route.fulfill({
        status: 429,
        body: JSON.stringify({ error: 'Rate limit exceeded' })
      });
    });

    await chatUtils.sendMessage('This should trigger rate limiting');

    // Should show rate limit message
    await expect(page.locator('[data-testid="rate-limit-message"]')).toBeVisible();
  });

  test('should handle authentication errors', async ({ page }) => {
    // Mock authentication error
    await page.route('**/bedrock-runtime/**', route => {
      route.fulfill({
        status: 401,
        body: JSON.stringify({ error: 'Unauthorized' })
      });
    });

    await chatUtils.sendMessage('This should trigger auth error');

    // Should redirect to login or show auth error
    await expect(page.locator('[data-testid="auth-error"]')).toBeVisible();
  });
});

