    // Should contain code block
    await expect(lastMessageElement.locator('pre code')).toBeVisible();

    // Should have syntax highlighting
    await expect(lastMessageElement.locator('.hljs')).toBeVisible();
  });
});

test.describe('Chat Error Handling', () => {
  let authUtils: AuthUtils;
  let chatUtils: ChatUtils;

  test.beforeEach(async ({ page }) => {
    authUtils = new AuthUtils(page);
    chatUtils = new ChatUtils(page);

    const testUsers = DataUtils.loadTestUsers();
    await authUtils.loginWithGoogle(testUsers.paid.email, testUsers.paid.password);
  });

  test('should handle API errors gracefully', async ({ page }) => {
    // Mock API error
    await page.route('**/bedrock-runtime/**', route => {
      route.fulfill({
        status: 500,
        body: JSON.stringify({ error: 'Internal Server Error' })
      });
    });

    await chatUtils.sendMessage('This should trigger an error');

    // Should show error message
    await expect(page.locator('[data-testid="error-message"]')).toBeVisible();
    await expect(page.locator('[data-testid="retry-button"]')).toBeVisible();
  });

  test('should handle rate limiting', async ({ page }) => {
    // Mock rate limit response
    await page.route('**/bedrock-runtime/**', route => {
      route.fulfill({
        status: 429,
        body: JSON.stringify({ error: 'Rate limit exceeded' })
      });
    });

    await chatUtils.sendMessage('This should trigger rate limiting');

    // Should show rate limit message
    await expect(page.locator('[data-testid="rate-limit-message"]')).toBeVisible();
  });

  test('should handle authentication errors', async ({ page }) => {
    // Mock authentication error
    await page.route('**/bedrock-runtime/**', route => {
      route.fulfill({
        status: 401,
        body: JSON.stringify({ error: 'Unauthorized' })
      });
    });

    await chatUtils.sendMessage('This should trigger auth error');

    // Should redirect to login or show auth error
    await expect(page.locator('[data-testid="auth-error"]')).toBeVisible();
  });
});

